mapper.select:
SELECT password
FROM role_user
WHERE username = #{username}

SELECT role
FROM role_user
WHERE username = #{username}

mapper.update:
UPDATE role_user
SET password = #{password}
WHERE username = #{username}

UPDATE role_user
SET ban = 1
WHERE username = #{username}

mapper.select:
SELECT ban
FROM role_user
WHERE username = #{username}

SELECT permission
FROM role_user
WHERE username = #{username}
